mapper.insert:
INSERT INTO chat_room_members (
	chat_room_no,
	user_id,
	trip_plan_no,
	is_chat_room_author
)
VALUES (
		   #{chatRoomNo},
		   #{userId},
		   #{tripPlanNo},
		   #{isChatRoomAuthor}
	   );

mapper.delete:
DELETE FROM chat_room_members
WHERE chat_room_no = #{chatRoomNo}
  AND user_id = #{userId}

mapper.update:
update chat_room_members
set status = 1
where chat_room_no = #{chatRoomNo}
    and user_id = #{userId}
	and #{isChatRoomAuthor} = 1
