mapper.update:
update chat_rooms
set
    chat_room_status = #{chatRoomStatus}
where
	chat_room_no = #{chatRoomNo}

mapper.select:
select * from (SELECT cr.*, tp.trip_plan_no,tp.trip_days, tp.trip_plan_title, ROW_NUMBER() OVER (ORDER BY cr.chat_room_no) AS row_seq FROM chat_rooms cr RIGHT JOIN ( SELECT crm.chat_room_no, crm.trip_plan_no FROM chat_room_members crm
chat_room_title like '%${searchKeyword}'
GROUP BY crm.chat_room_no, crm.trip_plan_no ) crm ON cr.chat_room_no = crm.chat_room_no LEFT JOIN trip_plan tp ON crm.trip_plan_no = tp.trip_plan_no ORDER BY cr.chat_room_no) AS temp WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}
