mapper.select:
SELECT * FROM ( SELECT BOARD_NO, BOARD_AUTHOR, BOARD_TITLE, BOARD_CONTENTS, BOARD_VIEWS, BOARD_REG_DATE, QNA_CATEGORY, IS_QNA_ANSWERED, QNA_ANSWER_CONTENTS, BOARD_CATEGORY, ROW_NUMBER() OVER (ORDER BY CASE WHEN IS_QNA_ANSWERED = 1 THEN 0 ELSE 1 END, BOARD_NO DESC) AS row_seq FROM board WHERE BOARD_CATEGORY = 1
AND BOARD_TITLE LIKE CONCAT('%', #{searchKeyword}, '%')
) AS derived_table WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT BOARD_NO, BOARD_AUTHOR, BOARD_TITLE, BOARD_CONTENTS, BOARD_VIEWS, BOARD_REG_DATE, QNA_CATEGORY, IS_QNA_ANSWERED, QNA_ANSWER_CONTENTS, BOARD_CATEGORY
FROM board
WHERE BOARD_NO = #{qnaNo}

mapper.update:
UPDATE board
SET IS_QNA_ANSWERED = 1,
    QNA_ANSWER_CONTENTS = #{qnaAnswerContents}
WHERE BOARD_NO = #{qnaNo}

UPDATE board
SET BOARD_TITLE = #{qnaTitle},
    BOARD_CONTENTS = #{qnaContents},
    QNA_CATEGORY = #{qnaCategory}
WHERE BOARD_NO = #{qnaNo}
