mapper.select:
SELECT * FROM ( SELECT tp.trip_plan_no, tp.trip_plan_author, tp.trip_plan_title, tp.trip_plan_thumbnail, tp.trip_days, tp.trip_plan_reg_date, tp.trip_plan_del_date, tp.is_plan_deleted, tp.is_plan_public, tp.is_plan_downloadable, tp.is_trip_completed, tp.trip_plan_likes, tp.trip_plan_views, GROUP_CONCAT(DISTINCT dp.daily_plan_no) AS daily_plan_nos, GROUP_CONCAT(DISTINCT dp.daily_plan_contents) AS daily_plan_contents, GROUP_CONCAT(DISTINCT dp.total_trip_time) AS total_trip_times, GROUP_CONCAT(DISTINCT p.place_no) AS place_nos, GROUP_CONCAT(DISTINCT p.place_tags) AS place_tags, GROUP_CONCAT(DISTINCT p.place_coordinates) AS place_coordinates, GROUP_CONCAT(DISTINCT p.place_image) AS place_images, GROUP_CONCAT(DISTINCT p.place_phone_number) AS place_phone_numbers, GROUP_CONCAT(DISTINCT p.place_address) AS place_addresses, GROUP_CONCAT(DISTINCT p.place_category) AS place_categories, GROUP_CONCAT(DISTINCT p.trip_time) AS trip_times, GROUP_CONCAT(DISTINCT p.trip_path) AS trip_paths, ROW_NUMBER() OVER ( ORDER BY
tp.trip_plan_views DESC, tp.trip_plan_no DESC
tp.trip_plan_likes DESC, tp.trip_plan_no DESC
tp.trip_plan_reg_date DESC, tp.trip_plan_no DESC
) AS row_seq FROM trip_plan tp LEFT JOIN daily_plan dp ON tp.trip_plan_no = dp.trip_plan_no LEFT JOIN place p ON dp.daily_plan_no = p.daily_plan_no WHERE
tp.trip_plan_author = #{tripPlanAuthor}
tp.is_plan_public = TRUE AND tp.is_plan_deleted = FALSE
AND tp.trip_plan_author NOT IN
#{user}
GROUP BY tp.trip_plan_no ) subquery WHERE row_seq BETWEEN #{search.startRowNum} AND #{search.endRowNum};

mapper.update:
UPDATE trip_plan
SET is_plan_public = #{isPlanPublic}
WHERE trip_plan_no = #{tripPlanNo}

UPDATE trip_plan
SET is_plan_downloadable = #{isPlanDownloadable}
WHERE trip_plan_no = #{tripPlanNo}

UPDATE trip_plan
SET is_plan_deleted = #{isPlanDeleted},
trip_plan_del_date = #{tripPlanDelDate}
WHERE trip_plan_no = #{tripPlanNo}

UPDATE trip_plan
SET is_trip_completed = #{isTripCompleted}
WHERE trip_plan_no = #{tripPlanNo}

UPDATE trip_plan
SET trip_plan_likes = #{tripPlanLikes}
WHERE trip_plan_no = #{tripPlanNo}

UPDATE trip_plan
SET trip_plan_views = #{tripPlanViews}
WHERE trip_plan_no = #{tripPlanNo}
