mapper.insert:
INSERT
INTO users( user_id, nickname, pwd, user_name, phone, addr, addr_detail, email, ssn, self_intro, user_photo,
            gender, age, role, is_secession, secession_date, is_suspension, suspension_date, warning_count,
            is_self_intro_public, is_user_photo_public, is_using_memo_bar, is_listing_attached_memo,
            is_listing_shared_memo )
VALUES	 (	#{userId}, #{nickname}, #{pwd}, #{userName}, #{phone}, #{addr}, #{addrDetail}, #{email}, #{ssn}, #{selfIntro}, #{userPhoto},
            #{gender}, #{age}, #{role}, #{isSecession}, #{secessionDate}, #{isSuspension}, #{suspensionDate}, #{warningCount},
            #{isSelfIntroPublic}, #{isUserPhotoPublic}, #{isUsingMemoBar}, #{isListingAttachedMemo},
            #{isListingSharedMemo}
         );

mapper.select:
SELECT * FROM ( SELECT user_id, user_name, nickname, user_reg_date, is_secession, ROW_NUMBER() OVER (ORDER BY user_id) AS row_seq FROM users
user_id like CONCAT('%', #{searchKeyword}, '%')
user_name like CONCAT('%', #{searchKeyword}, '%')
nickname like CONCAT('%', #{searchKeyword}, '%')
) AS derived_table WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

mapper.update:
UPDATE users
pwd = #{pwd}, nickname = #{nickname}, phone = #{phone}, addr = #{addr}, addr_detail = #{addrDetail}, email = #{email}, self_intro = #{selfIntro}, user_photo = #{userPhoto}, is_self_intro_public = #{isSelfIntroPublic}, is_user_photo_public = #{isUserPhotoPublic},
WHERE user_id = #{userId}

UPDATE users

    secession_Date  = #{secessionDate},
    is_secession    = #{isSecession}

WHERE user_id = #{userId}

mapper.select:
SELECT count(*)
FROM users
WHERE user_id = #{userId}

SELECT count(*)
FROM users
WHERE nickname = #{nickname}

SELECT user_id
FROM users
WHERE phone = #{phone}
